import Card from "../ui/Card";
import classes from "./NewFightForm.module.css";
import { useRef } from "react";
import Select from "react-select";
import { useState, useEffect } from "react";
import axios from 'axios';

function NewFightForm(props) {
  const methodInputRef = useRef();

  const [loadedFighters, setLoadedFighters] = useState([]);

  console.log("PROBA");

  useEffect(() => {
    axios({
      method: "get",
      url: "fighters",
    }).then((response) => {
      let fighters = response.data;
      let ret = [];
      for (let i in fighters) {
        ret.push({value: fighters[i].id});
      }
      setLoadedFighters(ret);
      // console.log("LOADED",loadedFighters);
    });
  }, []);

  console.log("Loaded fighters", loadedFighters)

  function submitHandler(event) {
    event.preventDefault();
    const enteredMethod = methodInputRef.current.value;

    const fighterData = {
      id: 3,
      name: "Simeun",
    };

    const fightData = {
      method: enteredMethod,
      redCornerFighter: fighterData,
    };

    console.log(fightData);
    props.onAddFight(fightData);
  }

  const options = [
    { value: "chocolate", label: "Chocolate" },
    { value: "strawberry", label: "Strawberry" },
    { value: "vanilla", label: "Vanilla" },
  ];

  return (
    <Card>
      <form className={classes.form} onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor="method">Fight Finish Method</label>
          <input type="text" required id="method" ref={methodInputRef} />
        </div>
        <div>
          <label htmlFor="redCornerFighter">Red corner fighter</label>
          <Select options={options} />
        </div>
        <div className={classes.actions}>
          <button>Add Fight</button>
        </div>
      </form>
    </Card>
  );
}
export default NewFightForm;
