  const [redCornerFighterWins, setRedCornerFighterWins] = useState(null);
  const [blueCornerFighterWins, setBlueCornerFighterWins] = useState(null);

  const [redCornerFighterLosses, setRedCornerFighterLosses] = useState(null);
